package accepted.Q211_Q220;

import org.junit.Test;

/**
 * Created by FJ on 11/9/2015.
 * <p/>
 * Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it.
 * Find and return the shortest palindrome you can find by performing this transformation.
 * <p/>
 * For example:
 * <p/>
 * Given "aacecaaa", return "aaacecaaa".
 * <p/>
 * Given "abcd", return "dcbabcd".
 */
public class Q214 {

	public String shortestPalindrome(String s) {

		if (s == null || s.length() <= 1) {
			return s;
		}

		int maxIdx = 0;
		for (int i = s.length() - 1; i >= 0; i--) {
			if (isPalindrome(s, 0, i)) {
				maxIdx = i;
				break;
			}
		}

		return new StringBuilder(s.substring(maxIdx + 1)).reverse().append(s).toString();
	}

	private boolean isPalindrome(String s, int i, int j) {

		int mi = i + (j - i) / 2, mj = (j - i) % 2 == 0 ? mi : mi + 1;

		while (i <= mi) {
			if (s.charAt(mi) != s.charAt(mj)) {
				return false;
			}
			mi--;
			mj++;
		}
		return true;
	}

	@Test
	public void test() {
		System.out.println(shortestPalindrome("aacecaaa"));
		System.out.println(shortestPalindrome("abcd"));
		System.out.println(shortestPalindrome("ba"));
		String s = "";
		System.out.println(shortestPalindrome(s));

	}
}
